import useForm from '../lib/useForm';
import Form from './styles/Form';

export default function CreateProduct() {
  const { inputs, handleChange, clearForm, resetForm } = useForm({
    name: 'Product',
    price: 1000,
    description: 'Product description',
  });
  return (
    <Form>
      <fieldset disabled>
        <label htmlFor="name">
          Name
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Name"
            value={inputs.name}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="price">
          Price
          <input
            type="number"
            id="price"
            name="price"
            placeholder="price (pence)"
            value={inputs.price}
            onChange={handleChange}
          />
        </label>

        <button type="submit" onClick={resetForm}>
          + Add Product
        </button>
      </fieldset>
    </Form>
  );
}
